import 'package:decoding_text_effect/decoding_text_effect.dart';import 'package:flutter/material.dart';void main() => runApp(MyApp());class MyApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    return MaterialApp(      debugShowCheckedModeBanner: false,      home: Demo(),    );  }}class Demo extends StatefulWidget {  @override  _DemoState createState() => _DemoState();}class _DemoState extends State<Demo> {  int index = 0;    List<String> myText = [    'This is Text number\n One for demo',    'Welcome to the channel\n do share',    'Same text for more effect\n lol',    'Same text for more effect\n lol 2',  ];  void changeText(){  setState(() {    index++;    if(index == myText.length) index = 0;  });    }    @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text('Decoding effect'),      ),      body: SingleChildScrollView(        child: Container(          width: MediaQuery.of(context).size.width,          child:Column(            crossAxisAlignment: CrossAxisAlignment.center,            children: [              SizedBox(height: 20,),              Text('Decoding Effect Style Number 1'),              Container(                height: 200,                width: 250,                color: Colors.blue.shade200,                padding: EdgeInsets.all(30),                child: DecodingTextEffect(                    myText[index],                     decodeEffect: DecodeEffect.fromStart,                  textStyle: TextStyle(fontSize: 30),                ),              ),              SizedBox(height: 10,),              Text('Decoding Effect Style Number 2'),              Container(                height: 200,                width: 250,                color: Colors.red.shade200,                padding: EdgeInsets.all(30),                child: DecodingTextEffect(                  myText[index],                  decodeEffect: DecodeEffect.fromEnd,                  textStyle: TextStyle(fontSize: 30),                ),              ),              SizedBox(height: 10,),              Text('Decoding Effect Style Number 3'),              Container(                height: 200,                width: 250,                color: Colors.blue.shade200,                padding: EdgeInsets.all(30),                child: DecodingTextEffect(                  myText[index],                  decodeEffect: DecodeEffect.all,                  textStyle: TextStyle(fontSize: 30),                ),              ),              SizedBox(height: 10,),              Text('Decoding Effect Style Number 4'),              Container(                height: 200,                width: 250,                color: Colors.blue.shade200,                padding: EdgeInsets.all(30),                child: DecodingTextEffect(                  myText[index],                  decodeEffect: DecodeEffect.toMiddle,                  textStyle: TextStyle(fontSize: 30),                ),              ),              SizedBox(height: 10,),              Text('Decoding Effect Style Number 5'),              Container(                height: 200,                width: 250,                color: Colors.blue.shade200,                padding: EdgeInsets.all(30),                child: DecodingTextEffect(                  myText[index],                  decodeEffect: DecodeEffect.random,                  textStyle: TextStyle(fontSize: 30),                ),              ),            ],          ),        ),      ),      floatingActionButton: FloatingActionButton(          onPressed: changeText,        child: Icon(Icons.refresh),      ),    );  }}